    public Map<String, Object> getData() {
        Map<String, Object> data = new HashMap<String, Object>();
        data.put("apellido", tfApellido.getText().trim());
        data.put("nombre", tfNombre.getText().trim());
        data.put("direccion", tfDireccion.getText().trim());
        data.put("tele1", tfTele1.getText().trim());
        data.put("tele2", tfTele2.getText().trim());
        data.put("email", tfEmail.getText().trim());
        data.put("observacion", taObservacion.getText().trim());
        data.put("activo", checkActivo.isSelected());
        return data;
    }

    public void setData(Map<String, Object> data) {
        tfApellido.setText((String) data.get("apellido"));
        tfNombre.setText((String) data.get("nombre"));
        tfDireccion.setText((String) data.get("direccion"));
        tfTele1.setText((String) data.get("tele1"));
        tfTele2.setText((String) data.get("tele2"));
        tfEmail.setText((String) data.get("email"));
        taObservacion.setText((String) data.get("observacion"));
        checkActivo.setSelected((Boolean) data.get("activo"));
    }
